import React, { useEffect, useState } from "react";
import { useParams }                  from "react-router-dom";
import axios                          from "axios";
import "../styles/PostDetails.css";   // create this file for your CSS

const PostDetails = () => {
  const { id } = useParams();
  const [post, setPost] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:8080/posts/${id}`)
      .then(res => setPost(res.data))
      .catch(err => console.error(err));
  }, [id]);

  if (!post) return <div>Loadingâ€¦</div>;

  return (
    <div className="post-details">
      <h1 className="pd-title">{post.title}</h1>
      <h4 className="pd-author">Autor: {post.user.name || post.authorName}</h4>
      <div className="pd-images">
        {(post.mediaFiles || post.imageUrls).map((mf, i) => {
          // if you have full DTO: mf is string URL; otherwise mf.id
          const src = mf.imageUrl
            ? mf.imageUrl
            : `http://localhost:8080/media/${mf.id}/view`;
          return (
            <img
              key={i}
              src={src}
              alt={post.title}
              className="pd-image"
            />
          );
        })}
      </div>
      <p className="pd-description">{post.description}</p>
    </div>
  );
};

export default PostDetails;
