// src/components/PostDetails.jsx
import React, { useEffect, useState, useContext } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";

export default function PostDetails() {
  const { id } = useParams();
  const { user, token } = useContext(AuthContext);
  const navigate = useNavigate();
  const [post, setPost] = useState(null);

  useEffect(() => {
    if (!token) {
      navigate("/login");
      return;
    }

    axios
      .get(`http://localhost:8080/posts/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
      .then(res => {
        console.log("ðŸ” post details:", res.data);
        setPost(res.data);
      })
      .catch(err => {
        console.error(err);
        navigate("/"); // dacÄƒ nu existÄƒ sau e eroare
      });
  }, [id, token, navigate]);

  if (!post) return <div>Loadingâ€¦</div>;

  // compari user.id cu authorId preluat din DTO
  const isAuthor = user?.id === post.authorId;

  return (
    <div className="post-details">
      <h1 className="pd-title">{post.title}</h1>
      <h4 className="pd-author">Autor: {post.authorName}</h4>

      <div className="pd-images">
        {(post.imageUrls || []).map((url, i) => (
          <img
            key={i}
            className="pd-image"
            src={url}
            alt={post.title}
          />
        ))}
      </div>

      <p className="pd-description">{post.description}</p>

      {isAuthor && (
        <Link to={`/posts/${id}/edit`} className="edit-button">
          EditeazÄƒ postarea
        </Link>
      )}
    </div>
  );
}
