// src/components/CreatePostComp.jsx
import React, { useState, useContext, useEffect } from "react";
import { useNavigate }                        from "react-router-dom";
import { AuthContext }                        from "../context/AuthContext";
import "../styles/CreatePost.css";

export default function CreatePostComp() {
  const { user, token, isLoading } = useContext(AuthContext);
  const navigate                   = useNavigate();

  // redirect la login dacă nu ești autentificat
  useEffect(() => {
    if (!isLoading && !token) navigate("/login");
  }, [token, isLoading, navigate]);

  const [title, setTitle]           = useState("");
  const [description, setDescription] = useState("");
  const [files, setFiles]           = useState([]);
  const [feedback, setFeedback]     = useState("");

  const handleSubmit = async e => {
    e.preventDefault();
    setFeedback("");

    if (!user?.id) {
      setFeedback("⚠️ Trebuie să te autentifici ca să creezi o postare.");
      return;
    }

    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("userId", user.id);
    files.forEach(f => formData.append("files", f));

    try {
      const res = await fetch("http://localhost:8080/posts/create", {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` },
        body: formData
      });
      if (!res.ok) throw new Error(await res.text());
      const created = await res.json();
      setFeedback("✅ Postarea a fost creată!");
      setTimeout(() => navigate(`/posts/${created.id}`), 1200);
    } catch (err) {
      console.error(err);
      setFeedback(`⚠️ Eroare la creare: ${err.message}`);
    }
  };

  return (
    <div className="post-details">
      <form onSubmit={handleSubmit} className="edit-post-form">
        {/* Titlu */}
        <div className="pd-title">
          <input
            type="text"
            placeholder="Titlu..."
            value={title}
            onChange={e => setTitle(e.target.value)}
            required
          />
        </div>

        {/* Autor */}
        <h4 className="pd-author">Autor: {user?.name}</h4>

        {/* Descriere */}
        <div className="pd-description">
          <textarea
            placeholder="Descriere..."
            value={description}
            onChange={e => setDescription(e.target.value)}
            required
          />
        </div>

        {/* Feedback */}
        {feedback && <div className="feedback">{feedback}</div>}

        {/* Upload multiplu */}
        <div className="file-input">
          <label>
            Adaugă imagini:
            <input
              type="file"
              multiple
              onChange={e => setFiles(Array.from(e.target.files))}
            />
          </label>
        </div>

        {/* Buton submit */}
        <button type="submit" className="edit-button">
          Creează postarea
        </button>
      </form>
    </div>
  );
}
