import React, { useRef, useEffect } from 'react'
import * as THREE from 'three'

export default function Main() {
  const mountRef = useRef(null)

  useEffect(() => {
    const currentMount = mountRef.current

    // Scene setup
    const scene = new THREE.Scene()
    const camera = new THREE.PerspectiveCamera(
      75,
      currentMount.clientWidth / currentMount.clientHeight,
      0.1,
      1000
    )
    camera.position.z = 3

    const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true })
    renderer.setSize(currentMount.clientWidth, currentMount.clientHeight)
    currentMount.appendChild(renderer.domElement)

    // Cube geometry and material
    const geometry = new THREE.BoxGeometry()
    const material = new THREE.MeshNormalMaterial() // colorful material
    const cube = new THREE.Mesh(geometry, material)
    scene.add(cube)

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate)
      cube.rotation.x += 0.01
      cube.rotation.y += 0.01
      renderer.render(scene, camera)
    }
    animate()

    // Cleanup on unmount
    return () => {
      currentMount.removeChild(renderer.domElement)
      renderer.dispose()
    }
  }, [])

  return (
    <main className="main-container">
      <div className="text-content">
        <h2>Welcome to MyThreeJS</h2>
        <p>
          A React + Three.js demo inspired by the official threejs.org site.
          Rotate the cube below and explore 3D graphics on the web!
        </p>
      </div>
      <div className="canvas-container" ref={mountRef} />
    </main>
  )
}
