import React, { useState, useEffect, useContext } from "react";
import { useParams, useNavigate }            from "react-router-dom";
import { AuthContext }                       from "../context/AuthContext";
import "../styles/PostDetails.css";

export default function EditPost() {
  const { id }          = useParams();
  const { user, token } = useContext(AuthContext);
  const navigate        = useNavigate();

  const [post, setPost]           = useState(null);
  const [title, setTitle]         = useState("");
  const [description, setDescription] = useState("");
  const [feedback, setFeedback]   = useState("");

  // fetch initial data
  useEffect(() => {
    if (!token) return navigate("/login");
    fetch(`http://localhost:8080/posts/${id}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(r => r.json())
      .then(dto => {
        setPost(dto);
        setTitle(dto.title);
        setDescription(dto.description);
      })
      .catch(() => navigate("/"));
  }, [id, token, navigate]);

  const handleSubmit = async e => {
    e.preventDefault();
    setFeedback("");

    // update text
    const form = new URLSearchParams();
    form.append("title", title);
    form.append("description", description);
    const res = await fetch(`http://localhost:8080/posts/update/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
        Authorization: `Bearer ${token}`
      },
      body: form.toString()
    });

    if (res.ok) {
      setFeedback("✅ Modificările au fost salvate!");
      setTimeout(() => navigate(`/posts/${id}`), 1200);
    } else {
      const err = await res.text();
      setFeedback(`⚠️ Eroare: ${err}`);
    }
  };

  if (!post) return <div>Loading…</div>;

  return (
    <div className="post-details">
      <form onSubmit={handleSubmit} style={{width: "100%"}}>
        {/* Title */}
        <div className="pd-title">
          <input
            type="text"
            value={title}
            onChange={e => setTitle(e.target.value)}
            style={{
              width: "100%",
              border: "none",
              background: "transparent",
              fontSize: "2rem",
              fontWeight: "bold",
              color: "#333"
            }}
          />
        </div>

        {/* Author */}
        <h4 className="pd-author">Autor: {post.authorName}</h4>

        {/* Images */}
        <div className="pd-images">
          {(post.imageUrls || []).map((url, i) => (
            <img
              key={i}
              className="pd-image"
              src={url}
              alt={title}
            />
          ))}
        </div>

        {/* Description */}
        <div className="pd-description">
          <textarea
            value={description}
            onChange={e => setDescription(e.target.value)}
            style={{
              width: "100%",
              height: "auto",
              minHeight: "120px",
              border: "none",
              background: "transparent",
              resize: "vertical",
              color: "#444",
              fontSize: "1rem",
              lineHeight: "1.6"
            }}
          />
        </div>

        {/* Feedback */}
        {feedback && (
          <div
            style={{
              margin: "1rem 0",
              padding: "0.75rem",
              background: feedback.startsWith("✅") ? "#e0f7e9" : "#fdecea",
              color: feedback.startsWith("✅") ? "#064e3b" : "#611a15",
              borderRadius: "4px"
            }}
          >
            {feedback}
          </div>
        )}

        {/* Save button */}
        <button
          type="submit"
          style={{
            display: "inline-block",
            padding: "0.75rem 1.5rem",
            backgroundColor: "#04a2ff",
            color: "#fff",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
            transition: "background 0.2s"
          }}
        >
          Salvează
        </button>
      </form>
    </div>
  );
}
