// src/context/AuthContext.js
import React, { createContext, useState, useEffect } from "react";
import jwtDecode from "jwt-decode";

export const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);

  // La montare, verifică dacă există un token salvat și populează starea
  useEffect(() => {
    const savedToken = localStorage.getItem("token");
    if (savedToken) {
      try {
        const { name, exp } = jwtDecode(savedToken);
        if (exp * 1000 > Date.now()) {
          setToken(savedToken);
          setUser({ name });
        } else {
          localStorage.removeItem("token");
        }
      } catch {
        localStorage.removeItem("token");
      }
    }
  }, []);

  // Păstrează compatibilitatea: login continuă să decodeze și să seteze user
  const login = (newToken) => {
    localStorage.setItem("token", newToken);
    setToken(newToken);
    const { name } = jwtDecode(newToken);
    setUser({ name });
  };

  const logout = () => {
    localStorage.removeItem("token");
    setToken(null);
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, token, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
}
