import React from "react";
import jwt_decode from "jwt-decode";
import "../styles/Header.css";

const Header = () => {
  const token = localStorage.getItem("token");
  let name = null;

  if (token) {
    try {
      // decodăm payload-ul JWT
      const payload = jwt_decode(token);
      // extragem claim-ul "name" pe care l-ai pus în backend
      name = payload.name;
    } catch (e) {
      console.error("Token JWT invalid sau expirat:", e);
      // opțional: poți și să ștergi tokenul invalid
      // localStorage.removeItem("token");
    }
  }

  return (
    <header className="app-header">
      {name && <div className="user-greeting">Salut, {name}!</div>}
    </header>
  );
};

export default Header;
