import React, { useState } from "react";
import { FaEye, FaEyeSlash } from "react-icons/fa";
import "../styles/Register.css";

function Register() {
  const [email, setEmail] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  const handleRegister = async (e) => {
  e.preventDefault();

  try {
    const response = await fetch("http://localhost:8080/auth/register", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        email: email,
        name: username, // name corespunde c√¢mpului din entitatea `User`
        password: password,
      }),
    });

    if (response.ok) {
      alert("Cont creat cu succes! Te po»õi autentifica.");
      // Po»õi face redirect aici spre /login dacƒÉ vrei
    } else {
      const err = await response.text();
      alert("Eroare la √Ænregistrare: " + err);
    }
  } catch (error) {
    console.error("Eroare de re»õea:", error);
    alert("Eroare de re»õea.");
  }
};
  return (
    <div className="register-container">
      <form className="register-form" onSubmit={handleRegister}>
        <h2>Creare cont</h2>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Nume utilizator"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <div className="password-field">
          <input
            type={showPassword ? "text" : "password"}
            placeholder="ParolƒÉ"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <span
            className="password-toggle-icon"
            onClick={() => setShowPassword(!showPassword)}
            title={showPassword ? "Ascunde parola" : "AratƒÉ parola"}
          >
            {showPassword ? "üôà" : "üëÅÔ∏è"}
          </span>
        </div>
        <button type="submit">√énregistreazƒÉ-te</button>
      </form>
    </div>
  );
}

export default Register;
