// src/components/CreatePostComp.jsx
import React, { useState, useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

export default function CreatePostComp() {
  const { user, token, isLoading } = useContext(AuthContext);
  const navigate = useNavigate();

  // redirect la /login dacÄƒ nu eÈ™ti logat
  useEffect(() => {
    if (!isLoading && !token) {
      navigate("/login");
    }
  }, [token, isLoading, navigate]);

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [files, setFiles] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Debug: verificÄƒ user.id
    console.log("ðŸ”¥ Creare post pentru user:", user);

    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("userId", String(user.id));     // <<< obligatoriu
    files.forEach((file) => formData.append("files", file));

    // Debug: vezi exact ce trimiÈ›i
    for (let [key, val] of formData.entries()) {
      console.log(key, "=", val);
    }

    try {
      const res = await fetch("http://localhost:8080/posts/create", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,    // doar bearer, fÄƒrÄƒ Content-Type
        },
        body: formData,
      });

      if (res.ok) {
        const createdPost = await res.json();
        navigate(`/posts/${createdPost.id}`);
      } else {
        const err = await res.text();
        alert("Eroare la creare postare: " + err);
      }
    } catch (err) {
      console.error("Eroare de reÈ›ea:", err);
      alert("Eroare de reÈ›ea.");
    }
  };

  return (
    <div className="create-post-container">
      <h2>CreeazÄƒ o nouÄƒ postare</h2>
      <form onSubmit={handleSubmit} className="create-post-form">
        <label>
          Titlu:
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </label>
        <label>
          Descriere:
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </label>
        <label>
          Imagini/Vid:
          <input
            type="file"
            multiple
            onChange={(e) => setFiles(Array.from(e.target.files))}
          />
        </label>
        <button type="submit">Trimite</button>
      </form>
    </div>
  );
}
