// src/pages/EditPost.jsx
import React, { useState, useEffect, useContext } from "react";
import { useParams, useNavigate }           from "react-router-dom";
import { AuthContext }                       from "../context/AuthContext";
import "../styles/PostDetails.css";  // stilurile de la vizualare

export default function EditPost() {
  const { id }           = useParams();
  const { token, user }  = useContext(AuthContext);
  const navigate         = useNavigate();

  const [title, setTitle]         = useState("");
  const [description, setDescription] = useState("");
  const [files, setFiles]         = useState([]);
  const [feedback, setFeedback]   = useState("");

  useEffect(() => {
    fetch(`http://localhost:8080/posts/${id}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(r => r.json())
      .then(post => {
        setTitle(post.title);
        setDescription(post.description);
      })
      .catch(() => navigate("/"));
  }, [id, token, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    // … trimite PUT + media la fel ca înainte …
  };

  return (
    <div className="post-details"> 
      {/* folosești containerul de vizualizare */}
      <form onSubmit={handleSubmit} className="edit-post-form">
        {/* Titlu */}
        <div className="pd-title">
          <label>
            Titlu:
            <input
              type="text"
              value={title}
              onChange={e => setTitle(e.target.value)}
            />
          </label>
        </div>

        {/* Autor (rămâne static) */}
        <h4 className="pd-author">Autor: {user.name}</h4>

        {/* Imagini existente */}
        <div className="pd-images">
          {/* ... */}
        </div>

        {/* Descriere */}
        <div className="pd-description">
          <label>
            Descriere:
            <textarea
              value={description}
              onChange={e => setDescription(e.target.value)}
            />
          </label>
        </div>

        {/* Fișiere noi */}
        <div className="pd-images">
          <label>
            Adaugă imagini:
            <input
              type="file"
              multiple
              onChange={e => setFiles(Array.from(e.target.files))}
            />
          </label>
        </div>

        <button type="submit" className="edit-button">
          Salvează
        </button>

        {feedback && <div className="feedback success">{feedback}</div>}
      </form>
    </div>
  );
}
