// src/pages/EditPost.jsx
import React, { useState, useEffect, useContext } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import "../styles/EditPost.css";

export default function EditPost() {
  const { id } = useParams();
  const { token } = useContext(AuthContext);
  const navigate = useNavigate();

  const [title, setTitle]       = useState("");
  const [description, setDescription] = useState("");
  const [files, setFiles]       = useState([]);      // ← stocăm imaginile noi
  const [feedback, setFeedback] = useState("");
  const [loading, setLoading]   = useState(false);

  useEffect(() => {
    setLoading(true);
    fetch(`http://localhost:8080/posts/${id}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(dto => {
        setTitle(dto.title);
        setDescription(dto.description);
      })
      .catch(() => navigate("/"))
      .finally(() => setLoading(false));
  }, [id, token, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFeedback("");
    setLoading(true);

    // 1) Actualizează titlu și descriere
    const formText = new URLSearchParams();
    formText.append("title", title);
    formText.append("description", description);

    try {
      const resText = await fetch(`http://localhost:8080/posts/update/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
          "Authorization": `Bearer ${token}`
        },
        body: formText.toString()
      });
      if (!resText.ok) {
        const errText = await resText.text();
        throw new Error("Edit text failed: " + errText);
      }

      // 2) Dacă au fost încărcate fișiere, trimitem la add-media
      if (files.length > 0) {
        const mediaForm = new FormData();
        files.forEach(f => mediaForm.append("files", f));

        const resMedia = await fetch(`http://localhost:8080/posts/${id}/add-media`, {
          method: "POST",
          headers: { Authorization: `Bearer ${token}` },
          body: mediaForm
        });
        if (!resMedia.ok) {
          const errText = await resMedia.text();
          throw new Error("Upload media failed: " + errText);
        }
      }

      setFeedback("✅ Postarea a fost editată cu succes!");
      setTimeout(() => navigate(`/posts/${id}`), 1500);

    } catch (err) {
      console.error(err);
      setFeedback("⚠️ " + err.message);
    } finally {
      setLoading(false);
    }
  };

  if (loading && !feedback) return <p>Loading…</p>;

  return (
    <div className="page edit-page">
      <h2>Editează postarea</h2>
      {feedback && (
        <div className={`feedback ${feedback.startsWith("✅") ? "success" : "error"}`}>
          {feedback}
        </div>
      )}

      <form onSubmit={handleSubmit} className="edit-post-form">
        <label>
          Titlu:
          <input
            type="text"
            value={title}
            onChange={e => setTitle(e.target.value)}
            required
          />
        </label>
        <label>
          Descriere:
          <textarea
            value={description}
            onChange={e => setDescription(e.target.value)}
            required
          />
        </label>
        <label>
          Imagini noi:
          <input
            type="file"
            multiple
            onChange={e => setFiles(Array.from(e.target.files))}
          />
        </label>
        <button type="submit" disabled={loading}>
          {loading ? "Se salvează…" : "Salvează"}
        </button>
      </form>

      <Link to={`/posts/${id}`} className="back-link">Înapoi la detalii</Link>
    </div>
  );
}
