import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const PostDetails = () => {
  const { id } = useParams();
  const [post, setPost] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:8080/posts/${id}`)
      .then(res => {
        console.log("ðŸ” post details:", res.data);
        setPost(res.data);
      })
      .catch(err => console.error(err));
  }, [id]);

  if (!post) return <div>Loadingâ€¦</div>;

  return (
    <div className="post-details">
      <h1 className="pd-title">{post.title}</h1>
      <h4 className="pd-author">Autor: {post.user?.name ?? "Necunoscut"}</h4>
      <div className="pd-images">
        {post.mediaFiles.map((mf, i) => (
          <img
            key={i}
            className="pd-image"
            src={`http://localhost:8080/media/${mf.id}/view`}
            alt={post.title}
          />
        ))}
      </div>
      <p className="pd-description">{post.description}</p>
    </div>
  );
};

export default PostDetails;
