import React, { useEffect, useState } from "react";
import axios from "axios";
import './Galerie.css';

const Galerie = () => {
  const [lucrari, setLucrari] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8080/posts/all")
      .then(res => setLucrari(res.data))
      .catch(err => console.error("Eroare la încărcarea lucrărilor", err));
  }, []);

  return (
    <div className="gallery">
      {lucrari.map(l => (
        <div key={l.id} className="gallery-item">
          <img src={l.imagine} alt={l.titlu} />
          <p>{l.titlu}</p>
        </div>
      ))}
    </div>
  );
};

export default Galerie;
