// src/pages/MyPosts.jsx
import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import { Link, useNavigate } from "react-router-dom";

export default function MyPosts() {
  const { user, token, isLoading } = useContext(AuthContext);
  const navigate = useNavigate();
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    if (!isLoading && !token) {
      navigate("/login");
      return;
    }
    if (user?.id) {
      fetch(`http://localhost:8080/posts/user/${user.id}`, {
        headers: { Authorization: `Bearer ${token}` }
      })
        .then(res => res.json())
        .then(data => setPosts(data))
        .catch(err => console.error(err));
    }
  }, [user, token, isLoading, navigate]);

  return (
    <div className="page">
      <h2>Postările mele</h2>
      {posts.length === 0 ? (
        <p>Nu ai postări încă.</p>
      ) : (
        <ul>
          {posts.map(p => {
            // folosește un fallback pentru description
            const desc = p.description || "";
            // ia maxim 50 caractere
            const excerpt = desc.length > 50
              ? desc.substring(0, 50) + "..."
              : desc;
            return (
              <li key={p.id}>
                <Link to={`/posts/${p.id}`}>
                  <strong>{p.title}</strong> — {excerpt}
                </Link>
              </li>
            );
          })}
        </ul>
      )}
    </div>
  );
}
