import React, { useState, useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import "../styles/CreatePost.css";

export default function CreatePostComp() {
  const { user, token, isLoading } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    if (!isLoading && !token) navigate("/login");
  }, [token, isLoading, navigate]);

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [videoUrl, setVideoUrl] = useState("");
  const [tags, setTags] = useState(""); // ‚úÖ TAGS
  const [files, setFiles] = useState([]);
  const [previews, setPreviews] = useState([]);
  const [feedback, setFeedback] = useState("");

  useEffect(() => {
    if (!files.length) {
      setPreviews([]);
      return;
    }
    const urls = files.map(f => URL.createObjectURL(f));
    setPreviews(urls);
    return () => urls.forEach(u => URL.revokeObjectURL(u));
  }, [files]);

  const removeImage = i => {
    setFiles(prev => prev.filter((_, idx) => idx !== i));
  };

  const extractVideoID = url => {
    const m = url.match(/[?&]v=([^&]+)/);
    return m ? m[1] : null;
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setFeedback("");

    if (!user?.id) {
      setFeedback("‚ö†Ô∏è Trebuie sƒÉ te autentifici.");
      return;
    }

    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("videoUrl", videoUrl);
    formData.append("userId", user.id);
    formData.append("tags", tags); // ‚úÖ TAGS
    files.forEach(f => formData.append("files", f));

    try {
      const res = await fetch("http://localhost:8080/posts/create", {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` },
        body: formData
      });

      if (!res.ok) throw new Error(await res.text());

      const created = await res.json();
      setFeedback("‚úÖ Postarea a fost creatƒÉ!");
      setTimeout(() => navigate(`/posts/${created.id}`), 1200);
    } catch (err) {
      console.error(err);
      setFeedback(`‚ö†Ô∏è Eroare la creare: ${err.message}`);
    }
  };

  return (
    <div className="post-details">
      <form onSubmit={handleSubmit} className="edit-post-form">
        <div className="pd-title">
          <input
            type="text"
            placeholder="Titlu..."
            value={title}
            onChange={e => setTitle(e.target.value)}
            required
          />
        </div>

        <h4 className="pd-author">Autor: {user?.name}</h4>

        <div className="pd-description">
          <textarea
            placeholder="Descriere..."
            value={description}
            onChange={e => setDescription(e.target.value)}
            required
          />
        </div>

        {/* üîπ Tag input */}
        <div className="tag-field">
          <label>
            Taguri (ex: game, lab, unity):
            <input
              type="text"
              placeholder="ex: game,lab,ai"
              value={tags}
              onChange={e => setTags(e.target.value)}
            />
          </label>
        </div>

        <div className="video-field">
          <label>
            Link YouTube:
            <input
              type="url"
              placeholder="https://www.youtube.com/watch?v=..."
              value={videoUrl}
              onChange={e => setVideoUrl(e.target.value)}
            />
          </label>
        </div>

        {extractVideoID(videoUrl) && (
          <div className="video-preview">
            <iframe
              src={`https://www.youtube.com/embed/${extractVideoID(videoUrl)}`}
              title="YouTube preview"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            />
          </div>
        )}

        {previews.length > 0 && (
          <div className="preview-images">
            {previews.map((url, i) => (
              <div key={i} className="preview-item">
                <img src={url} alt={`preview ${i}`} className="preview-img" />
                <button
                  type="button"
                  className="remove-btn"
                  onClick={() => removeImage(i)}
                >
                  √ó
                </button>
              </div>
            ))}
          </div>
        )}

        {feedback && <div className="feedback">{feedback}</div>}

        <div className="file-input">
          <label>
            AdaugƒÉ imagini:
            <input
              type="file"
              multiple
              accept="image/*"
              onChange={e => {
                const sel = Array.from(e.target.files);
                setFiles(prev => [...prev, ...sel]);
              }}
            />
          </label>
        </div>

        <button type="submit" className="edit-button">
          CreeazƒÉ postarea
        </button>
      </form>
    </div>
  );
}
