// src/pages/EditPost.jsx
import React, { useState, useEffect, useContext } from "react";
import { useParams, useNavigate }           from "react-router-dom";
import { AuthContext }                       from "../context/AuthContext";
import "../styles/EditPost.css";  // aici vom pune şi stil pentru feedback

export default function EditPost() {
  const { id }           = useParams();
  const { token }        = useContext(AuthContext);
  const navigate         = useNavigate();

  const [title, setTitle]           = useState("");
  const [description, setDescription] = useState("");
  const [feedback, setFeedback]     = useState("");    // ** nou **
  const [loading, setLoading]       = useState(false);

  // Prefill
  useEffect(() => {
    setLoading(true);
    fetch(`http://localhost:8080/posts/${id}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(dto => {
        setTitle(dto.title);
        setDescription(dto.description);
      })
      .catch(() => navigate("/"))
      .finally(() => setLoading(false));
  }, [id, token, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFeedback("");    // curăţăm mesajul anterior
    setLoading(true);

    const form = new URLSearchParams();
    form.append("title", title);
    form.append("description", description);

    try {
      const res = await fetch(`http://localhost:8080/posts/update/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
          "Authorization": `Bearer ${token}`
        },
        body: form.toString()
      });

      if (res.ok) {
        setFeedback("✅ Postarea a fost editată cu succes!");
        // după 1.5s redirect la detalii
        setTimeout(() => navigate(`/posts/${id}`), 1500);
      } else {
        const text = await res.text();
        setFeedback(`⚠️ Eroare la editare: ${text}`);
      }
    } catch (err) {
      console.error(err);
      setFeedback("⚠️ Eroare de rețea. Te rog încearcă din nou.");
    } finally {
      setLoading(false);
    }
  };

  if (loading && !feedback) return <p>Loading…</p>;

  return (
    <div className="page edit-page">
      <h2>Editează postarea</h2>

      {/* 1. Afișăm feedback-ul */}
      {feedback && (
        <div className={`feedback ${feedback.startsWith("✅") ? "success" : "error"}`}>
          {feedback}
        </div>
      )}

      <form onSubmit={handleSubmit} className="edit-post-form">
        <label>
          Titlu:
          <input
            type="text"
            value={title}
            onChange={e => setTitle(e.target.value)}
            required
          />
        </label>
        <label>
          Descriere:
          <textarea
            value={description}
            onChange={e => setDescription(e.target.value)}
            required
          />
        </label>
        <button type="submit" disabled={loading}>
          {loading ? "Se salvează…" : "Salvează"}
        </button>
      </form>
    </div>
  );
}
