 import React, { useState, useContext, useEffect } from "react";
 import { useNavigate } from "react-router-dom";
 import { AuthContext } from "../context/AuthContext";

 export default function CreatePostComp() {
   const { user, token } = useContext(AuthContext);
   const navigate = useNavigate();

   // dacă nu ești logat, redirect la login
   useEffect(() => {
     if (!token) navigate("/login");
   }, [token, navigate]);

   const [title, setTitle] = useState("");
   const [description, setDescription] = useState("");
   const [files, setFiles] = useState([]);

   const handleSubmit = async (e) => {
     e.preventDefault();

     const formData = new FormData();
     formData.append("title", title);
     formData.append("description", description);
     formData.append("userId", String(user.id));       // ← aici adaugi ID-ul user-ului
     files.forEach((file) => formData.append("files", file));

     try {
       const res = await fetch("http://localhost:8080/posts/create", {
         method: "POST",
         headers: {
           Authorization: `Bearer ${token}`,
         },
         body: formData,
       });

       if (res.ok) {
         const createdPost = await res.json();
         navigate(`/posts/${createdPost.id}`);
       } else {
         const err = await res.text();
         alert("Eroare la creare postare: " + err);
       }
     } catch (err) {
       console.error(err);
       alert("Eroare de rețea.");
     }
   };

   return (
     <div className="create-post-container">
       <h2>Creează o nouă postare</h2>
       <form onSubmit={handleSubmit} className="create-post-form">
         <label>
           Titlu:
           <input
             type="text"
             value={title}
             onChange={e => setTitle(e.target.value)}
             required
           />
         </label>
         <label>
           Descriere:
           <textarea
             value={description}
             onChange={e => setDescription(e.target.value)}
             required
           />
         </label>
         <label>
           Imagini/Vid:
           <input
             type="file"
             multiple
             onChange={e => setFiles(Array.from(e.target.files))}
           />
         </label>
         <button type="submit">Trimite</button>
       </form>
     </div>
   );
 }
