// src/components/Posts.js
import React, { useEffect, useState } from "react";
import PostCard from "./PostCard";
import "../styles/Post.css";
import axios from "axios";
import SearchBar from "./SearchBar"; // ← componenta nouă

const Posts = ({ showSearchBar = false }) => {
  const [posts, setPosts] = useState([]);

  // încărcare inițială: toate postările
  useEffect(() => {
    fetchAllPosts();
  }, []);

  const fetchAllPosts = async () => {
    try {
      const res = await axios.get("http://localhost:8080/posts/all");
      setPosts(res.data);
    } catch (err) {
      console.error("Eroare la încărcarea postărilor:", err);
    }
  };

  const handleSearch = async (filters) => {
    try {
      const res = await axios.get("http://localhost:8080/posts/search", {
        params: filters,
      });
      setPosts(res.data);
    } catch (err) {
      console.error("Eroare la filtrare:", err);
    }
  };

  return (
    <div className="posts-container">
      {showSearchBar && <SearchBar onSearch={handleSearch} />}
      {posts.map((post) => (
        <PostCard
          key={post.id}
          id={post.id}
          title={post.titlu || post.title} // suport pentru ambele formate
          imageUrl={post.imagineUrl || (post.imageUrls?.[0] ?? "")} // fallback dacă vine din search
        />
      ))}
    </div>
  );
};

export default Posts;
